(function () {

    'use strict';

    function ApiAnalyser (server) {

        var _this = this;

        this.apis = {};

        this.register = function registerAPI (apiName, apiProcessor) {
            if (apiProcessor instanceof server.constructor.API) {
                this.apis[apiName] = apiProcessor;
            } else {
                server.console.error('Object "apiProcessor" should extends APIProcessor.');
            }
        };

        this.find = function findAPI (apiName) {
            return _this.apis[apiName];
        };



    }

    module.exports = ApiAnalyser;

}());
